import { DroidUser, DroidScore } from 'osu-droid-scraping';

/**
 * Parameters for osu!droid profile card generation.
 */
interface DroidCardParameters {
	/**
	 * An osu!droid user, obtained via the module `osu-droid-scraping`.
	 *	 
	 * * https://github.com/floemia/osu-droid-scraping
	 */
	user?: DroidUser,
	/**
	 * An array of `DroidScore`, obtained via the module `osu-droid-scraping`.
	 * 
	 * https://github.com/floemia/osu-droid-scraping
	 */
	scores?: DroidScore[],

	/**
	 * The uid of the osu!droid account. If passed, all of the other parameters will be discarded.
	 *
	 */
	uid?: number
}

/**
 * Generates an osu!droid profile card with relevant data, statistics and top plays.
 *
 * @param params Parameters for card generation.
 */
declare const card: (params: DroidCardParameters) => Promise<Buffer | undefined>;

declare const _default: {
    card: (params: DroidCardParameters) => Promise<Buffer | undefined>;
};

export { DroidCardParameters, card, _default as default };
