/**
* An osu!droid user profile.
**/
interface DroidUser {
	/**
	* The username of the account.
	**/
	username: string,

	/**
	* The URL of the user's avatar.
	**/
	avatar_url: string,

	/**
	* The UID of the account.
	**/
	id: number,

	/**
	* Contains the user's global ranks ( score | dpp ).
	**/
	rank: {
		/**
		* The user's score rank.
		**/
		score: number,

		/**
		* The user's DPP rank.
		**/
		dpp: number,
	},

	/**
	* The user's country code (ISO-3166-1 A-2).
	* 
	* https://en.wikipedia.org/wiki/List_of_ISO_3166_country_codes
	**/
	country: string,

	/**
	* The user's total ranked score.
	**/
	ranked_score: number,

	/**
	* The user's total DPP (Droid Performance Points).
	**/
	dpp: number,

	/**
	* The user's hit accuracy.
	**/
	accuracy: number,

	/**
	* The user's total playcount.
	**/
	playcount: number
}

/**
* Parameters for osu!droid user fetching.
**/
interface DroidUserParameters {
	/**
	* UID of the osu!droid account.
	**/
	uid: number,

	/**
	* Optional. The HTML source code of the user's profile page. It's used to avoid unnecessary requests.
	**/
	response?: string,
}

/**
* An osu!droid score.
**/
interface DroidScore {
	/**
	* The default title of the beatmap.
	* 
	* It should only be used if needed, as it may contain typos. Use `hash` to find the beatmap instead.
	**/
	title: string,

	/**
	* The achieved rank of the score.
	* 
	* One of these => `["XH" | "X" | "SH" | "S" | "A" | "B" | "C" | "D"]`
	**/
	rank: string,

	/**
	* The total score obtained.
	**/
	score: number,

	/**
	* Unix timestamp of when the score was set.
	**/
	timestamp: number,

	/**
	* The total DPP obtained from this score.
	* 
	* It should only be used if needed, as beatmaps that aren't ranked or whitelisted will return `NaN`. 
	* 
	* Please use this instead:
	* 
	* https://github.com/Rian8337/osu-droid-module/tree/master/packages/osu-difficulty-calculator
	**/
	dpp: number,

	/**
	* The hit accuracy from this score.
	**/
	accuracy: number,

	/**
	* The mods used in this score.
	**/
	mods: DroidMods,

	/**
	* The maximum combo obtained from this score.
	**/
	combo: number

	/**
	* The amount of misses from this score.
	**/
	misses: number,

	/**
	* The beatmap's MD5 checksum.
	* 
	* It can be used to obtain a beatmap using the osu! API.
	**/
	hash: string,

	/**
	* The author of this score.
	**/
	user: DroidUser,

}

/**
* Parameters for osu!droid score fetching.
**/
interface DroidScoreParameters {
	/**
	* UID of the osu!droid account.
	**/
	uid: number,

	/**
	* Optional. The HTML source code of the user's profile page. It's used to avoid unnecessary requests.
	**/
	response?: string,

	/**
	* Type of score to return.
	* 
	* Defaults to `"top"`.
	**/
	type: "top" | "recent"

	/**
	* Optional. Amount of scores to return `(1 <= x <= 50)`.
	* 
	* Defaults to 50.
	**/
	limit?: number,
}

/**
* An object containing a list of mods and the speed rate multiplier.
**/
interface DroidMods {
	/**
	* An array with abreviated mods.
	* 
	* `["HardRock", "Hidden", ...]` => `acronyms: ["HR", "HD", ...]` 
	* 
	* A No Mod play will return `[]`.
	**/
	acronyms: string[],

	/**
	* The speed rate applied on top of the mods.
	* 
	* `["HardRock", "Hidden", "1.75x"]` => `speed: 1.75`
	* 
	* Defaults to `speed: 1`.
	**/
	speed: number,
}

/**
 * Core of the module. It contains all of the functions.
*/
declare const droid: {
    /**
     * Given the `uid` of an osu!droid account, returns a `DroidUser` with all of their parsed info from `osu!droid.moe`.
     *
     * Returns `{ error: "User doesn't exist." }` if the user doesn't exist.
     *
     * @param {DroidScoreParameters} params - Parameters for osu!droid user fetching.
    */
    user: (params: DroidUserParameters) => Promise<DroidUser | {
        error: string;
    }>;
    /**
     * Given the `uid` of an osu!droid account, returns the user's scores, parsed from `osudroid.moe`.
     *
     * @param {DroidScoreParameters} params - Parameters for osu!droid user's scores fetching.
    */
    scores: (params: DroidScoreParameters) => Promise<DroidScore[] | {
        error: string;
    }>;
    /**
     * Given an array of mods (full name), returns `DroidMods`.
     *
     * It contains all of the mods' acronyms and the added speed rate multiplier.
     *
     * @param {string[]} mods_arr - Array with mods with their respective full names `( [HardRock, Hidden, ...] )`.
    */
    mods: (mods_arr: string[]) => DroidMods;
    /**
     * Given the `uid` of an osu!droid account, retrieves the HTML source code of the user's page from `osudroid.moe`.
     *
     * Returns `{ error: "User doesn't exist." }` if the user doesn't exist.
     * @param {number} uid - The UID of the osu!droid account.
    */
    request: (uid: number) => Promise<string | {
        error: string;
    }>;
};

declare const _default: {
    droid: {
        user: (params: DroidUserParameters) => Promise<DroidUser | {
            error: string;
        }>;
        scores: (params: DroidScoreParameters) => Promise<DroidScore[] | {
            error: string;
        }>;
        mods: (mods_arr: string[]) => DroidMods;
        request: (uid: number) => Promise<string | {
            error: string;
        }>;
    };
};

export { DroidMods, DroidScore, DroidScoreParameters, DroidUser, DroidUserParameters, _default as default, droid };
